import React from 'react';

import Grid from '../components/Grid';

import Jumbotron from 'react-bootstrap/Jumbotron';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

import FadeIn from 'react-fade-in';

import { shake } from 'react-animations';

import styled, { keyframes } from 'styled-components';

// Animation code derived from: https://www.npmjs.com/package/react-animations
const myAnimation = keyframes`${shake}`;
const Animation = styled.div`
  animation: 1s ${myAnimation};
`;

function HomePage(props) {
    const width = 10;
    window.scrollTo(0, 0);
    return (
        <FadeIn delay={400}>
            <Jumbotron className="bg-transparent jumbotron-fluid hero">
                <Container fluid={true}>
                    <Row className="justify-content-center py-5">
                        <Col md={width} sm={12}>
                            {props.animated && <h1 className="display-1"><Animation>{props.animated}</Animation></h1>}
                            {props.title && <h1 className="display-2 font-weight-bolder">{props.title}</h1>}
                            {/* {props.subTitle && <h2 className="display-4 font-weight-normal">{props.subTitle}</h2>} */}

                            {/* <h3 className="font-weight-normal mt-4 small-description">Designing for <a className="link-underline" href="https://trillproject.com/" target="_blank" rel="noreferrer">Trill Project</a> and studying <a className="link-underline" href="https://cs.illinois.edu/academics/undergraduate/degree-program-options/bs-statistics-computer-science" target="_blank" rel="noreferrer">CS + Statistics</a> at <a className="link-underline" href="https://illinois.edu" target="_blank" rel="noreferrer">UIUC</a>.</h3> */}
                        </Col>
                    </Row>
                </Container>
            </Jumbotron>

            <Grid projects={props.projects} />
        </FadeIn>
    );
}

export default HomePage;
