import React from 'react';

import AOS from 'aos';

import aboutpic from '../assets/aboutprofpic.jpg';

import Hero from '../components/Hero';

import Container from 'react-bootstrap/Container';

import FadeIn from 'react-fade-in';

function AboutPage(props) {
    AOS.init();
    const width = 8;
    window.scrollTo(0, 0);
    return (
        <FadeIn>
            <Hero title={props.title} width={width - 2} />
            <Container fluid={true}>
                <div class="content">
                    <div class="left" data-aos="fade-right">
                        <img
                            class="profile-image"
                            alt="Profile"
                            src={aboutpic}
                        />
                    </div>
                    <div class="right">
                        <p className="about-subtitle">
                            Hi there! I'm Bryan Ge.
                        </p>

                        <p className="about-body paragraph">
                            I am currently based in Dallas, TX and Champaign, IL. I am a freshman at the University of Illinois at Urbana-Champaign, majoring in Statistics and Computer Science and minoring in Business and Informatics.
                        </p>

                        <p>
                            Outside of school, I enjoy:
