import React from 'react';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';
import { useSpring, animated } from 'react-spring'

import CardInfo from '../components/CardInfo.js';

const calc = (x, y) => [-(y - window.innerHeight / 2) / 20, (x - window.innerWidth / 2) / 20, 1.1]
const trans = (x, y, s) => `perspective(600px) rotateX(${x}deg) rotateY(${y}deg) scale(${s})`

function Card(props) {
    return (
        <Link className="d-inline-block project-card" to={props.item.path} style={{ textDecoration: 'none', color: 'black'}}>
            <animated.div
                class="card"
                onMouseMove={({ clientX: x, clientY: y }) => set({ xys: calc(x, y) })}
                onMouseLeave={() => set({ xys: [0, 0, 1] })}
                style={{ transform: props.xys.interpolate(trans) }}
            />
            <img className="project-card-image" src={props.item.imgSrc} alt={props.item.imgSrc} />
            <CardInfo title={props.item.title} subTitle={props.item.subTitle} roles={props.item.roles} path={props.item.path} />
        </Link>
    );
}

export default Card;
