import React from 'react';
import { Link } from 'react-router-dom';
import { useSpring, animated } from 'react-spring'

import CardInfo from '../components/CardInfo.js';

// Animation code derived from https://codesandbox.io/embed/rj998k4vmm
const calc = (x, y) => [-(y - window.innerHeight / 2) / 100, (x - window.innerWidth / 2) / 100, 1.05]
const trans = (x, y, s) => `perspective(600px) rotateX(${x}deg) rotateY(${y}deg) scale(${s})`

function Card(props) {
    const [springProps, set] = useSpring(() => ({ xys: [0, 0, 1], config: { mass: 5, tension: 350, friction: 40 } }));

    let elem = document.querySelector('Link');
    let rect = elem.getBoundingClientRect();
    const minX = rect.left;
    const minY = rect.top;

    return (
        <Link className="d-inline-block project-card" to={props.item.path} style={{ textDecoration: 'none', color: 'black'}}>
            <div>

            </div>
            <CardInfo title={props.item.title} subTitle={props.item.subTitle} roles={props.item.roles} path={props.item.path} />
        </Link>
    );
}

export default Card;
