import React from 'react';

import Container from 'react-bootstrap/Container';

import Hero from '../components/Hero.js'

function ProjectTitle(props) {
    return (
        <Container fluid={true} className="project-title-container">
            <img src={props.info.imgSrc} alt={props.info.name} className="coverImg center" />
            <div className="bottom-left">
            <h2 className="display-4 font-weight-bolder text-white bottom-left">{props.info.name}</h2>
            <h3 className="display-5 font-weight-normal project-description text-white bottom-left">{props.info.description}</h3>
            </div>


            <Hero className="bottom-left" projectName={props.info.name} description={props.info.description} width={8} />
        </Container>
    );
}

export default ProjectTitle;
