import styled from 'styled-components'

export const Container = styled.div`
    padding: 16px 10px;
    background: #ededed;
`

export const Wrapper = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    max-width: 1000px;
    margin: 0 auto;
`

export const Row = styled.div`
    display: flex;
    flex-direction: row;
    text-align: left;
    max-left: 60px;
    margin: 0 auto;
`

export const Link = styled.a`
    text-decoration: none;
    font-size: 18px;
    margin-bottom: auto;
    color: #000;
    padding-right: 5%;

    &:hover {
        color: #0D6E99;
        transition: 200ms ease-in;
        text-decoration: none;
    }
`

export const Title = styled.p`
    font-size: 24px;
    color: #000;
    margin-bottom: 40px;
    font-weight: bold;
`
