import React from 'react';
import { BrowserRouter as Router, Route, Link } from 'react-router-dom';
import Container from 'react-bootstrap/Container';
import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';
import './App.css';

import leaf from './assets/leaf.png';
import iot from './assets/iot.png';
import das from './assets/dascover.png';
import mfb from './assets/miningforbias.png';

import Footer from './components/Footer'
import HomePage from './pages/HomePage'
import AboutPage from './pages/AboutPage'
import LeafPage from './pages/LeafPage.js';
import DasPage from './pages/DasPage.js';
import MiningForBiasPage from './pages/MiningForBiasPage.js';
import IotPage from './pages/IotPage.js';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      title: "Bryan Ge | Portfolio",
      headerLinks: [
        { title: 'Home', path: '/'},
        { title: 'About', path: '/about'},
        { title: 'Resume', path: '/resume'}
      ],
      home: {
        title: 'Hi! I\'m Bryan Ge.',
        subTitle: 'I am a programmer, designer, and photographer.',
      },
      about: {
        title: 'About Me'
      },
      projects: [
        {
            id: 0,
            name: 'Leaf',
            description: 'Interactive educational web app for the future generation to learn about their carbon footprint.',
            roles: 'UI/UX Designer, Software Developer',
            imgSrc: leaf,
            path: '/leaf',
            selected: false
        },
        {
            id: 1,
            name: 'Internet of Things Playground',
            description: 'Website where students can code Internet of Things devices and share them with the world.',
            roles: 'UI Designer',
            imgSrc: iot,
            path: '/iot-playground',
            selected: false
        },
        {
            id: 2,
            name: 'Daily Awareness Society',
            description: 'Blog dedicated to spreading awareness on social issues.',
            roles: 'Web Developer, Content Creator',
            imgSrc: das,
            path: '/daily-awareness-society',
            selected: false
        },
        {
            id: 3,
            name: 'Mining for Bias',
            description: 'Website that gives users bias information on news articles.',
            roles: 'Software Developer',
            imgSrc: mfb,
            path: '/mining-for-bias',
            selected: false
        }
      ]
    }
  }

  render() {
    return (
      <Router>
        <Container className="p=0 my-font" fluid={true}>
          <Navbar className="top-bar fixed" bg="transparent" expand="lg">
            <Navbar.Brand><Link className="nav-link default" to="/">Bryan Ge</Link></Navbar.Brand>
            <Navbar.Toggle className="border-0" aria-controls="navbar-toggle"/>
            <Navbar.Collapse id="navbar-toggle">
              <Nav className="ml-auto">
                <Link className="nav-link default" to="/">Home</Link>
                <Link className="nav-link default" to="/about">About</Link>
                <a className="nav-link default" href={"https://drive.google.com/file/d/1H4-sfzxQG9cpSsKMzuQUFK3NX5h8d0V5/view?usp=sharing"} target="_blank" rel="noreferrer">Resume</a>
              </Nav>
            </Navbar.Collapse>
          </Navbar>

          <Route path="/" exact render={() => <HomePage title={this.state.home.title} subTitle={this.state.home.subTitle}/>} />
          <Route path="/about" exact render={() => <AboutPage title={this.state.about.title} />} />

          <Route path="/leaf" exact render={() => <LeafPage name={this.state.projects[0].name} />} />
          <Route path="/iot" component={IotPage} />
          <Route path="/daily-awareness-society"  exact render={() => <DasPage name={this.state.projects[2].name} />} />
          <Route path="/mining-for-bias" component={MiningForBiasPage} />

          <Footer />

        </Container>
      </Router>
    );
  }
}

export default App;
