import React from 'react';
import Jumbotron from 'react-bootstrap/Jumbotron';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';

function Hero(props) {
    return(
        <Jumbotron className="bg-transparent jumbotron-fluid">
            <Container fluid={true}>
                <Row className="justify-content-center py-5">
                    <Col md={8} sm={12}>
                        { props.title && <h1 className="display-2 font-weight-bolder">{props.title}</h1> }
                        { props.subTitle && <h2 className="display-4 font-weight-normal">{props.subTitle}</h2> }
                        { props.projectName && <h2 className="display-4 font-weight-normal">{props.subTitle}</h2> }
                        { props.description && <h3 className="display-5 font-weight-light text-muted">{props.description}</h3>}
                    </Col>
                </Row>
            </Container>
        </Jumbotron>
    );
}

export default Hero;
