import React from 'react';
import { Link } from 'react-router-dom';
import { useSpring, animated } from 'react-spring'

import CardInfo from '../components/CardInfo.js';

// Animation code derived from https://codesandbox.io/embed/rj998k4vmm
const calc = (x, y) => [-(y - window.innerHeight / 2) / 100, (x - window.innerWidth / 2) / 100, 1.05]
const trans = (x, y, s) => `perspective(600px) rotateX(${x}deg) rotateY(${y}deg) scale(${s})`

function Card(props) {
    const [springProps, set] = useSpring(() => ({ xys: [0, 0, 1], config: { mass: 5, tension: 350, friction: 50 } }));

    var element = document.getElementById(this);
    var clientRect = element.getBoundingClientRect();
    var clientX = clientRect.left;
    var clientY = clientRect.top;

    return (
        <Link className="d-inline-block project-card" to={props.item.path} style={{ textDecoration: 'none', color: 'black'}}>
            <div>
                <animated.img
                    class="project-card-image"
                    onMouseMove={({ clientX: x, clientY: y }) => set({ xys: calc(x, y) })}
                    onMouseLeave={() => set({ xys: [0, 0, 1] })}
                    style={{ transform: springProps.xys.interpolate(trans) }}
                    src={props.item.imgSrc}
                    alt={props.item.imgSrc}
                />
            </div>
            <CardInfo title={props.item.name} subTitle={props.item.description} roles={props.item.roles} path={props.item.path} />
        </Link>
    );
}

export default Card;
