import React, { useState } from 'react';
import Grid from '../components/Grid';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Col from 'react-bootstrap/Col';
import FadeIn from 'react-fade-in';
// import { merge, fadeInRight, fadeInDown } from 'react-animations';
// import styled, { keyframes } from 'styled-components';
import Sticky from 'react-sticky-el';

import bottomimage from '../assets/other/bottomimage.png';
import topcursor from '../assets/other/topcursor.png';

// Animation code derived from: https://www.npmjs.com/package/react-animations
// const myAnimation = keyframes`${merge(fadeInDown, fadeInRight)}`;
// const Animation = styled.div`
//   animation: 1.5s ${myAnimation};
// `;

function HomePage(props) {
    const [filter, setFilter] = useState("all");

    const width = 9;
    const portfolio = props.projects;

    let filteredItems = portfolio.filter(item => item.category.includes(filter));

    return (
        <FadeIn delay={300} className="background">
            <Container fluid={true}>
                <div>
                    <Row className="justify-content-center home-top">
                        <Col md={width} sm={12}>
                            <a href="https://www.bryange.com/about" id="circleme" rel="noopener noreferrer"><img src={bottomimage} className="home-image" alt="Home Profile Pic" /></a>

                            {/* <div className="home-images">
                                    <a href="https://www.bryange.com/about" id="circleme" rel="noopener noreferrer"><img src={bottomimage} className="home-image" alt="Home Profile Pic" /></a>
                                    <section id="cursor">
                                        <a href="https://www.bryange.com/about" rel="noopener noreferrer"><img src={topcursor} className="home-image-cursor top-image" alt="Figma Cursor" aria-label="Hi, I'm Bryan!" /></a>
                                    </section>
                                </div> */}

                            <div className='right'>

                                {props.title && <h1 className="display-6 home-title">I'm a digital product designer with a love for baking and social good.</h1>}

                                <div className="home-positions center">
                                    <h2 className="home-role"><span role='img' aria-label='cloud'>☁️</span> Currently, I am interning at <a className="link-underline" href="https://apple.com/" target="_blank" rel="noopener noreferrer">Apple</a> as a Product Designer on the iCloud team!</h2>
                                    <h2 className="home-role"><span role='img' aria-label='paint palette'>🎨</span> During the school year, I enjoy designing for nonprofits and building design culture at <a className="link-underline" href="https://uiuc.hack4impact.org/" target="_blank" rel="noopener noreferrer">Hack4Impact</a>.</h2>
                                    <h2 className="home-role"><span role='img' aria-label='eyes'>👀</span> I'm also seeking a part-time product design or design engineer role for the fall.</h2>
                                </div>
                            </div>

                            <Col>
                                <Row>



                                </Row>
                            </Col>
                        </Col>
                    </Row>
                    <Row>
                    <Col md={width} sm={12} className="projects">
                        <Grid projects={filteredItems} />
                    </Col>
                    </Row>

                </div>
            </Container>
        </FadeIn >
    );
}

export default HomePage;
