import React from 'react';

import Row from 'react-bootstrap/Row';
import Container from 'react-bootstrap/Container';

import Card from '../components/Card.js';

import leaf from '../assets/leaf.png';
import iot from '../assets/iot.png';
import das from '../assets/dascover.png';
import mfb from '../assets/miningforbias.png';

class Grid extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            items: [
                {
                    id: 0,
                    title: 'Leaf',
                    subTitle: 'Interactive educational web app for the future generation to learn about their carbon footprint.',
                    roles: 'UI/UX Designer, Software Developer',
                    imgSrc: leaf,
                    path: '/leaf',
                    selected: false
                },
                {
                    id: 1,
                    title: 'Internet of Things Playground',
                    subTitle: 'Website where students can code Internet of Things devices and share them with the world.',
                    roles: 'UI Designer',
                    imgSrc: iot,
                    path: '/iot-playground',
                    selected: false
                },
                {
                    id: 2,
                    title: 'Daily Awareness Society',
                    subTitle: 'Blog dedicated to spreading awareness on social issues.',
                    roles: 'Web Developer, Content Creator',
                    imgSrc: das,
                    path: '/daily-awareness-society',
                    selected: false
                },
                {
                    id: 3,
                    title: 'Mining for Bias',
                    subTitle: 'Website that gives users bias information on news articles.',
                    roles: 'Software Developer',
                    imgSrc: mfb,
                    path: '/mining-for-bias',
                    selected: false
                }
            ]
        }
    }

    handleCardClick = (path, card) => {
        // let items = [...this.state.items];
        // items[id].selected = items[id].selected ? false : true;
        // items.forEach(item => {
        //     if (item.id !== id) {
        //         item.selected = false;
        //     }
        // });

        // this.setState({
        //     items
        // });
    }

    makeItems = (items) => {
        return items.map(item => {
            return <Card item={item} click={(e => this.handleCardClick(item.path, e))} key={item.id}/>
        })
    }

    render() {
        return (
            <Container fluid={true}>
                <Row className="justify-content-around">
                    {this.makeItems(this.state.items)}
                </Row>
            </Container>
        )
    }
}

export default Grid;
